UPDATE QUESTION SET QUESTION_ID = QUESTION_ID + 10000;


DROP SEQUENCE SEQ_QUESTION_ID;

DECLARE 
  max_id NUMBER;
BEGIN
  SELECT MAX(QUESTION_ID) + 1 INTO max_id FROM QUESTION;
  EXECUTE IMMEDIATE 'CREATE SEQUENCE SEQ_QUESTION_ID  INCREMENT BY 1 START WITH ' || TO_CHAR(max_id) ||
    ' MAXVALUE 99999999 MINVALUE 1 NOCYCLE NOCACHE NOORDER';
END;
